try {

	var dbConn = getDBConnectionInbound();

	var xmlmsg = new XML(SerializerFactory.getSerializer('HL7V2').toXML(connectorMessage.getRawData()));

	// Commit all segments of a file or nothing if something goes wrong
	dbConn.setAutoCommit(false);

	dbConn.executeUpdate("INSERT INTO rap_hl7 VALUES ('"+ $('messageHeader_messageControlId_value') + "',$ยง$" +  xmlmsg  + "$ยง$,'" + DateUtil.getCurrentDate('yyyy-MM-dd HH:mm:ss')+"')");

	dbConn.executeUpdate('COMMIT');

} catch(e) {
	dbConn.executeUpdate('ROLLBACK');

	logger.error(e.message);
	as = new AlertSender(connectorMessage);
	as.sendAlert(e.message + " \nFile " + $('originalFilename'));
	return new Response(ERROR, e.message, 'RAP-SIU-PGSQL-INBOUND', e.message + " \nFile " + $('originalFilename'));

} finally {
	// Close DB connection
	if (dbConn) {
		dbConn.close();
	}
}

